sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   if(newValue == 'mainframe'){
	g_form.setValue('access_category', 'mainframe');
   }
   if(newValue == 'webdevelop'){
	g_form.setValue('access_category', 'webdev');
   }
   if(newValue == 'prjmanage'){
	g_form.setValue('access_category', 'prjmanage');
   }

}
